import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ClientModel } from 'src/app/models/client-model';
import { TaskModel } from 'src/app/models/task-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent {

  public clients:ClientModel[]=[]
  public task=new TaskModel()

  public constructor(private service:DataService, private router:Router){}

   public async ngOnInit() {
    try {

      this.clients= await this.service.getAllClients()

    } catch (error:any) {
      alert(error.message)
    }
  }

  public async send(){
    try {
      await this.service.addTask(this.task)
      alert("you added task")
      this.router.navigateByUrl("/list")

    } catch (error:any) {
      alert(error.message)
    }
  }
}
<form (ngSubmit)="send()" ngNativeVslidetion>

    <select name="clientId" [(ngModel)]="task.clientId" >

        <option selected disabled value="">Select Client:</option>
        <option *ngFor="let c of clients" value={{c._id}}>{{c.name}}</option>
    </select>

    <label>Task Description:</label>
    <input type="text" name="description" [(ngModel)]="task.description" required>

    <label>Date</label>
    <input type="datetime-local" name="c" [(ngModel)]="product.creationTime" required>

    <label>Eexpiry:</label>
    <input type="datetime-local" name="expiry" [(ngModel)]="product.expiry" required >

    <label>Price:</label>
    <input type="number" name="price" [(ngModel)]="product.price" required min="0">

    <button>Add</button>

</form>
